INSERT INTO `theloai` (`id`, `ten_the_loai`, `mo_ta`, `slug_the_loai`, `trang_thai`, `created_at`, `updated_at`) VALUES
(0, 'Manga', 'Manga (tiếng Nhật: kanji: 漫画, hiragana: まんが, katakana: マンガ, Hán-Việt: mạn họa) là một cụm từ trong tiếng Nhật để chỉ các loại truyện tranh và tranh biếm họa. Manga được xem là từ đặc biệt để chỉ riêng truyện tranh xuất phát từ Nhật Bản.', 'manga', '1', '2022-01-18 18:25:32', '2022-05-03 06:00:11'),
(2, 'Light Novel', 'Light Novel (ライトノベル; raito noberu) là một dạng tiểu thuyết Nhật Bản chủ yêu nhắm đến đối tượng là học sinh trung học và phổ thông. Cái tên “Light Novel” là từ wasei-ego, một thuật ngữ Nhật Bản được biến tấu lại từ tiếng Anh. Tóm lại, Light Novel đôi lúc được gọi là ranobe (ラノベ) hoặc Light Novel ở phương Tây. Thường thì một cuốn tiểu thuyết dài không quá 40,000 đến 50,000 từ (nếu ngắn hơn thì sẽ được coi tương đương là tiểu thuyết ngắn (novella) theo điều khoản xuất bản của Hoa Kỳ), nhưng những cuốn tiểu thuyết dài khoảng hơn vài trăm trang thì khá hiếm, đa số những cuốn tiểu thuyết có lịch in ấn nghiêm ngặt thường được in theo khổ bunkobon (cỡ A6, 10.5 cm x 14.8 cm) và có tranh minh họa đính kèm.', 'light-novel', '1', '2022-01-18 18:25:56', '2022-01-18 18:25:56');
